    getTargetData(data){
        if(!data || !data.ad || data.ad.length == 0) return null;
        let ads = data.ad[0];
        let adm = JSON.parse(ads.admnative);
        let pics = [];
        for(let i = 1;i <= 10;i++){
            let pd = adm["image" + (i == 1 ? '' : i)]
            if(pd && pd.url){
                pics.push(pd.url);
            }
        }
        let result = {
            pics,
            cateId:-100,
            from:"广告",
            id:Math.random().toString().slice(3,8),
            time:"",
            title:adm.title,
            type:1,
            url:adm.click ? adm.click.url : '',
            displayurl:adm.displayurl
        }
        return result;
    }
